import{_ as t,c as o,o as n,ag as a}from"./chunks/framework.BgId8OS5.js";const f=JSON.parse('{"title":"align-content","description":"","frontmatter":{},"headers":[],"relativePath":"前端/flex布局/容器属性/6.多交叉轴对齐方式.md","filePath":"前端/flex布局/容器属性/6.多交叉轴对齐方式.md","lastUpdated":1749111669000}'),c={name:"前端/flex布局/容器属性/6.多交叉轴对齐方式.md"};function l(i,e,r,_,d,p){return n(),o("div",null,e[0]||(e[0]=[a('<h1 id="align-content" tabindex="-1">align-content <a class="header-anchor" href="#align-content" aria-label="Permalink to &quot;align-content&quot;">​</a></h1><p><strong>多个交叉轴的对齐方式</strong></p><p>大白话就是涉及到换行内容 <code>align-content</code>才会有效果 我们知道按照flex的默认属性 如果不设置 <code>flex-wrap</code> 为 <code>wrap</code>属性的话 flex容器里面的元素是不会换行的 如果元素太多 他只会收缩 那么就不会出现多个交叉轴 <code>align-content</code>就会没有一点效果</p><p>反之 <code>align-content</code>产生效果</p><ul><li>align-item 是控制元素在行上的排列</li><li>align-content 是控制行在交差轴上的排列</li></ul><p>属性和<code>align-item</code>是一样的</p><p>需要注意的是 由于默认值是 <code>stretch </code> 那么交叉轴会平分容器高度 如果子项也没有设置高度 那么子项的高度就是交叉轴的高度</p><blockquote><p>我好像明白了为什么一设置 flex-wrap 为 wrap 第一行和第二行的容器老是隔那么远 也不默认挨在一起</p></blockquote>',8)]))}const g=t(c,[["render",l]]);export{f as __pageData,g as default};
